                                            <Form.Label>Review</Form.Label>
                                            <Form.Control as="textarea" rows="4"/>
                                        </Form.Group>
                                        <Button type="submit">
                                            Submit
                                        </Button>
                                    </form>
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default Review;
