import React, {Component} from 'react';
import {Container, Row, Col, ToggleButtonGroup, ToggleButton, Button} from 'react-bootstrap'

import Review from './Reviews'
import Schedule from './Schedule'
import Features from './Features'

class App extends Component{

  state ={
    language: "English"
  }

  handleEnglish =(e)=>{
    this.setState({language:"French"})
  }
  handleFrench =(e)=>{
    this.setState({language:"English"})
  }

  render(){
    return (
      <div className="App">
        <ToggleButtonGroup type="radio" name="menu" defaultValue={this.state.language}>
          <ToggleButton value={"English"} onClick={this.handleEnglish}>English</ToggleButton>
          <ToggleButton value={"French"} onClick={this.handleFrench}>French</ToggleButton>
        </ToggleButtonGroup>
        <Button onClick={console.log(this.state.language)}>click me</Button>
        <br/>
        <Container>
          <Row>
            <Col>

            </Col>
          </Row>
        </Container>

      </div>
    );
  }
}
export default App;
